import { liquidityAggregator } from '@ai/liquidity-aggregator';

class AILiquidityAggregator {
  private aggregator: liquidityAggregator.Aggregator;

  constructor() {
    this.aggregator = new liquidityAggregator.Aggregator();
  }

  async getLiquidity(tokenAddress: string): Promise<number> {
    return this.aggregator.getLiquidity(tokenAddress);
  }

  async executeTrade(tokenAddress: string, amount: number): Promise<void> {
    return this.aggregator.executeTrade(tokenAddress, amount);
  }
}

export default AILiquidityAggregator;
