import { binanceSmartChain } from '@chain/binance-smart-chain';

class BinanceSmartChain {
  private web3: binanceSmartChain.providers.Web3Provider;

  constructor() {
    this.web3 = new binanceSmartChain.providers.Web3Provider('https://testnet.binance.org/api/v1');
  }

  async getBalance(address: string): Promise<number> {
    return this.web3.getBalance(address);
  }

  async sendTransaction(from: string, to: string, value: number): Promise<string> {
    return this.web3.sendTransaction({ from, to, value });
  }
}

export default BinanceSmartChain;
