import { bridge } from '@cross-chain/bridge';

class CrossChainBridge {
  private bridge: bridge.Bridge;

  constructor() {
    this.bridge = new bridge.Bridge();
  }

  async lockTokens(tokenAddress: string, amount: number): Promise<void> {
    return this.bridge.lockTokens(tokenAddress, amount);
  }

  async unlockTokens(tokenAddress: string, amount: number): Promise<void> {
    return this.bridge.unlockTokens(tokenAddress, amount);
  }
}

export default CrossChainBridge;
