import { analytics } from '@data/analytics';

class DecentralizedDataAnalytics {
  private analytics: analytics.Analytics;

  constructor() {
    this.analytics = new analytics.Analytics();
  }

  async getData(): Promise<any> {
    return this.analytics.getData();
  }

  async analyzeData(data: any): Promise<any> {
    return this.analytics.analyzeData(data);
  }
}

export default DecentralizedDataAnalytics;
