import { ethers } from '@chain/ethers';

class Ethereum {
  private web3: ethers.providers.Web3Provider;

  constructor() {
    this.web3 = new ethers.providers.Web3Provider('https://mainnet.infura.io/v3/YOUR_PROJECT_ID');
  }

  async getBalance(address: string): Promise<number> {
    return this.web3.getBalance(address);
  }

  async sendTransaction(from: string, to: string, value: number): Promise<string> {
    return this.web3.sendTransaction({ from, to, value });
  }
}

export default Ethereum;
