import { protocol } from '@interoperability/protocol';

class InteroperabilityProtocol {
  private protocol: protocol.Protocol;

  constructor() {
    this.protocol = new protocol.Protocol();
  }

  async addToken(tokenAddress: string): Promise<void> {
    return this.protocol.addToken(tokenAddress);
  }

  async removeToken(tokenAddress: string): Promise<void> {
    return this.protocol.removeToken(tokenAddress);
  }

  async transferToken(tokenAddress: string, from: string, to: string, amount: number): Promise<void> {
    return this.protocol.transferToken(tokenAddress, from, to, amount);
  }
}

export default InteroperabilityProtocol;
